<script data-cfasync="false" src="/cdn-cgi/scripts/5c5dd728/cloudflare-static/email-decode.min.js"></script><script type="8010cab0670225acc2358e71-text/javascript">
    const documents = {
                    website_tos:`Terms of service`,
                    privacy_policy:`Privacy policy`,
                    cookie_policy:`Cookies policy`,
                    refund_policy:`Refund policy`,
            };

    let headerHeight = "130px";

    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();

            const doc = document.querySelector(this.getAttribute('href'));

            if (doc) {
                window.scrollTo(0, doc.offsetTop - headerHeight)
            }
        });
    });

    function elementInViewport(el) {
        var top = el.offsetTop;
        var height = el.offsetHeight;

        return (
            top + height - 300 > window.pageYOffset &&
            window.pageYOffset > top - 300
        );
    }

    const checkDocumentIsInView = () => {
        document.querySelectorAll(".menu-link a").forEach((link) => {
            try {
                const type = link.attributes.href.value.replace(/^#/, "");
                const doc = document.querySelector(`#${type}`);

                const isInTheView = elementInViewport(doc);

                if (isInTheView) {
                    link.classList.add('active')
                    link.parentElement?.classList?.add('active')

                    document.title = `firstpagerank ${documents[type]}`
                } else {
                    link.classList.remove('active')
                    link.parentElement?.classList?.remove('active')
                }

            } catch {
            }
        })
    };

    setTimeout(() => {
        headerHeight = document.querySelector('header').offsetHeight;
        checkDocumentIsInView();
    }, 400);

    window.onscroll = checkDocumentIsInView;
</script>
